//地区
function getAreaLevel(id){
	var arr = new Array();
	if (id == '' || typeof id == 'undefined') return [];
	if (/\d{2}00/.test(id) && id.length==4) return [id];
	arr.push(id.substr(0, 2) + '00');
	if(id.length==6){
		var _id=id.substr(0, 4);
		if (_id=='1101' || _id=='1201' || _id=='3101' || _id=='5001'){
		}else{
			arr.push(_id);	
		}			
	}
	arr.push(id);
	return arr;
}
function getArea(id){
	var level = getAreaLevel(id);
	var l = __area;
	var area = null;
	for (var i in level){
		var id = level[i];
		for (var j in l){
			if (l[j].id == id){
				area = l[j];
				l = l[j].subAreas;
				break;
			}
		}
	}
	return area;
}
function getAreaName(id){
	var area = getArea(id);
	if (area){
		return area.name;
	}else{
		return '';
	}
}
function getFullAreaName(areaID)
{
	var name = '';
	var level = getAreaLevel(areaID);
	var l = __area;

	for (var i in level)
	{
		var id = level[i];
		var found = false;
		if (l)
		{
			for (var j in l)
			{
				if (l[j].id == id)
				{
					found = true;
					name += l[j].name;
					l = l[j].subAreas;
					break;
				}
			}
		}
		if (!found) break;
	}
	return name;
}
function getSubAreas(id)
{
	id = id || '';
	if (id == '') return __area;
	var area = getArea(id);
	if (area && area.subAreas)
	{
		return area.subAreas;
	} else
	{
		return [];
	}
}




