		{
			var doc = $(document);
			var wnd = $(window);
			var left = (wnd.width() - self.container.width()) / 2 + wnd.scrollLeft();
			var top = (wnd.height() - self.container.height()) / 2 + wnd.scrollTop();
			self.container.css({ left: left, top: top });
//			self.mask.css({ width: wnd.width(), height: doc.height() });
		};

